INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (1, 'It is important to avoid overwatering because the plant is succeptible to root rot. The plant should be watered after the top few inches of the potting soil have been allowed to dry out from the previous watering. The species will grow well if it is watered in a well draining pot.', 'It grows best if it is provided with a balanced fertilizer containing nitrogen phosporus and potassium. Diluted liquid fertilizer or organic granular fertilizer will work for fertilizing this species. For new plants, it is recomennded to fertilize every eight weeks for the first year, then decrease the number in successive years.', 'Trim the dead, diseased, overgrown branches in winter.', '{"spring", "summer", "fall"}', '{"fall", "winter"}', '{"cutting", "layering", "sowing"}', 'Needs excellent drainage in pots.');

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (2, '', 'Water calathea monthly, or every 3-4 weeks, with a half-strength balanced fertilizer or a liquid organic fertilizer, such as seaweed or compost tea. This is not necessary during the winter when the plant is in its dormant season.', '', '{}', '{}', '{"division"}', '');

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (3, "Pincushion peperomia likes moist soil and can be sensitive to over-watering, which can lead to rot. Under-watering leads to wilting. Potted Pincushion peperomia needs to be watered regularly during the growth season. Allow the soil to dry before watering. During the winter, water occasionally.", "Use a diluted liquid fertilizer bi-weekly for pincushion peperomia during spring. In the summer, you need only apply the solution once a month. It is not necessary to apply any fertilizer during the autumn or winter.", "Trim the diseased, withered leaves once a month.", '{"spring", "fall"}', '{}', '{"cutting"}', 'Flower pots.');

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (4, 'Zebra plant loves moisture and hates soaking wet feet, so frequent, even waterings are the best for your plant. When the top of the soil feels dry, thoroughly water your Zebra plant and allow the substrate to drain. It will likely need more frequent water during the growing season.', 'Zebra plant likes fertile soil, so frequent applications of fertilizer are recommended for this plant. Starting in spring and ending late summer, give your plant a balanced liquid fertilizer at 1/4 strength once every 2-4 weeks, depending on the quality of the soil. Be sure not to overfertilize, though!', 'Trim the diseased, withered leaves once a month.', '{"spring", "fall"}', '{}', '{"division"}', 'It is recommended to repot once every 1-2 years.');

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (5, "The Orange lily should be watered only when the top layer of soil feels dry to the touch. The most effective method for determining this is to stick a finger tip into the soil to feel whether it's dry or still moist. If the soil is dry, it is time to water again.", "The orange lily should be fertilized with a slow-release fertilizer on the top layer of soil. This species is considered a heavy feeder and will benefit from the addition of fertilizer to the soil. A commercial, slow-release fertilizer with a 14-14-14 nitrogen, phosphorous, and potassium formulation is sufficient. The plant should be fertilized at the time of planting, and then once monthly during the active spring and fall growing season.", '', '{}', '{"summer", "fall"}', '{"division", "sowing"}', '');

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (6, "Water Angel's-wings once every two to three weeks, letting the soil dry out between waterings. During its first season, however, keep the soil slightly moist to help the root system establish well. Stop watering entirely during winter.", "Fertilize angel's-wings once monthly with a balanced (such as 20-20-20) or low-nitrogen plant food. Stop fertilizer in late fall, and resume in early spring.", 'Shape the plant every 2 months during the growing season.', '{"spring"}', '{"spring", "summer", "winter"}', '{"cutting", "sowing"}', 'Needs excellent drainage in pots.');

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (7, "Golden pothos grows best in moist soil with medium humidity. When growing golden pothos, it is beneficial to mist the plant's leaves regularly with a spray bottle to maintain humidity around the leaf area. The top layer of soil should be allowed to dry between waterings, and the plant will grow best if it is kept in a pot with a draining hole in the bottom.", "Golden pothos requires fertilization for optimal growth. A water soluble fertilizer diluted by half is ideal for fertilizing this species. Between the spring and fall, golden pothos should be fertilized every couple of weeks. In the winter, fertilization can occur monthly. If the plant's leaves turn yellow, it is a sign that more fertilization is necessary.", 'Shape the plant every 2 months during the growing season.', '{"spring", "summer", "fall", "winter"}', '{}', '{"cutting", "layering"}', "Repot in the spring, choosing a container that's no more than 5 cm larger than the plant's rootball. Use a nutrient-rich, soil-based potting mix.");

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (8, "China rose is a very drought-tolerant type of rose but it still benefits from frequent watering during its flowering time. Water the lower section of the plant and the soil around it, without wetting the leaves, if possible. For roses planted in the ground, water until the area seems waterlogged and a small puddle forms around the stems. Water again when the top layer of soil feels dry.", "Fertilize your china rose with a balanced liquid fertilizer every two weeks during the growing period. Dilute the fertilizer into the water that you use for normal watering. You can also apply manure or garden compost in spring, during planting. Roses planted in containers or pots should be fertilized more often than those planted in the ground.", 'Trim the dead, diseased, overgrown branches in winter.', '{"spring", "fall"}', '{"fall"}', '{"cutting", "grafting", "sowing"}', "Increase pot diameter year by year, and replace pots before or after the winter each year. Masonry pots are preferred for potting.");

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (9, "The Florist's daisy should be watered to the extent that their soil is kept evenly moist at all times. When planted in a pot, it must be watered often and kept in a well-draining pot. Watering once or twice a week is sufficient for this species during the active growing period between spring and fall.", "The florist's daisy does not need to be fertilized in order to grow efficiently. If fertilization is preferred, a well-balanced, liquid houseplant fertilizer should be applied during waterings", 'Trim the diseased, withered leaves once a month.', '{"spring"}', '{"fall"}', '{"cutting", "sowing"}', 'Needs excellent drainage in pots.');

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (10, 'Average water needs, watering when the top 3 cm of soil has dried out.', 'Fertilization once every 2-3 months during the growing season.', '', '{"spring", "summer"}', '{}', '{"cutting", "layering"}', 'Potted plants should be repotted every 1-2 years.');

INSERT INTO care_guide(care_guide_id, water, fertilization, pruning, planting_time, harvest_time, propagation, potting_suggestions)
VALUES (11, 'During the growing season, water your Queen of the night whenever the surface of the soil dries out, making sure that the soil drains properly afterward. In winter, cut back watering by at least half.', "Apply a dose of diluted 20-20-20 liquid fertilizer to your queen of the night plant's soil every month from spring through fall. A lower-nitrogen fertilizer may also work well, as this plant is sensitive to nitrogen and may grow leggy stems and fewer blooms when over-fertilized.", 'Shape the plant every 2 months during the growing season.', '{"spring", "fall"}', '{"winter", "spring}', '{"cutting", "grafting", "sowing"}', '');

