INSERT INTO basic_plant_info(plant_id, botanical_name, known_as, species, description, poisonous, name_story, interesting_facts, symbolism, plant_characteristics_id, scientific_classification_id, plant_conditions_id, care_guide_id)
VALUES (1, 'monstera deliciosa', '{"tarovine", "fruit salad plant", "ceriman", "mexican breadfruit"}', 'swiss cheese plant', "The swiss cheese plant (Monstera deliciosa) produces bright, glossy leaves and makes a popular houseplant. It is originally native to tropical forest regions in Central America. The nickname swiss cheese plant refers to the small holes that develop in the plant's leaves. The long fruits resemble corncobs and smell sweet and fragrant when ripe.", true, {"Swiss cheese plant - This plant has always been loved for its unique leaves. During the seedling stage, its leaves are intact as a whole. However, as it ages, the leaves begin to pop out holes and lobes. The older it gets, the larger the holes and the larger the leaves are. This makes its leaves very similar looking to the holes in cheese, so it will be called Swiss cheese plant."}, '{"Its leaves are evergreen and extremly shade-tolerant. It is a famous indoor foliage plant. It is commonly used as potted ornamentals in Europe, America and Japan to decorate living rooms and windowsills."}', "Health, longevity, respect for one's elders", 1, 1, 1, 1);

INSERT INTO basic_plant_info(plant_id, botanical_name, known_as, species, description, poisonous, name_story, interesting_facts, symbolism, plant_characteristics_id, scientific_classification_id, plant_conditions_id, care_guide_id)
VALUES (2, 'goeppertia picturata', '{"variegated prayer plant"}', 'calathea', "Calathea picturata is a species of plant in the family Marantaceae, native to northwest Brazil. It is a clump-forming evergreen perennial growing to 35 to 40 cm. The leaves are dark green above, purple below, marked heavily with silver along the veins and midriff. It is tender, with a minimum temperature of 16 ℃ required, and in temperate areas is cultivated indoors as a houseplant. Under the synonym Goeppertia picturata the cultivar 'Argentea', with silver leaves edged in green, has gained the Royal Horticultural Society's Award of Garden Merit. ", false, '{}', '{}', '', 2, 2, 2, 2);

INSERT INTO basic_plant_info(plant_id, botanical_name, known_as, species, description, poisonous, name_story, interesting_facts, symbolism, plant_characteristics_id, scientific_classification_id, plant_conditions_id, care_guide_id)
VALUES (3, 'peperomia ferreyrae', '{"happy bean"}', 'pincushion peperomia', "Pincushion peperomia (Peperomia ferreyrae) is a perennial semi-succulent native to rainforests of South America. Pincushion peperomia is also commonly known as Happy Beans Peperomia and Green Beans Peperomia. This species is often grown as a houseplant and should be placed in bright indirect sunlight for optimal growth. Pincushion peperomia slightly moist soil, but is also very vulnerable to overwatering.", false, '{"Pincushion peperomia - The genus name Peperomia means name Pepper-like in Latin. That refers to the pepper-like shape of flowers of these plants. The first part of the common name, pincushion, refers to elongated, densely-packed pointy leaves, which makes it stand out from other species of the genus."}', '{"Despite the fact that the entire genus is named after hte flowers, peperomia ferreyraes clusters of small yellow flowers are not valued. Instead, the plant is cherished for its unique foliage. The small size and lack of tendency to spread makes it ideal for small spaces and arrangement - windowsills, dish gardens, desktops conservatories and greenhouses.}', 'Friendship and devotion', 3, 3, 3, 3);

INSERT INTO basic_plant_info(plant_id, botanical_name, known_as, species, description, poisonous, name_story, interesting_facts, symbolism, plant_characteristics_id, scientific_classification_id, plant_conditions_id, care_guide_id)
VALUES (4, 'goeppertia zebrina', '{}', 'zebra plant', "zebra plant (goeppertia zebrina) is an evergreen perennial that will grow from 61 to 91 cm tall, with light green leaves accented by dark green stripes. It blooms in summer with purple or white flowers. Prefers partial sun to full shade. Propagate this plant by dividing it every 2 to 3 years. Requires excellent drainage and high humidity. Grows best in indoor gardens and large terrariums.", false, '{"Zebra plant - The emerald green leaves of Goeppertia zebrina are covered in dark green stripes that look like the stripes of a zebra. Therefore, Goeppertia zebrina got its common name zebra plant. The specific epithet "zebrina" in its botanical name means "of zebra" and it refers to its variegated leaves.}', '{"The zebra plant leaf surface is velvety, and is dark green with yellow-green stripes. The back of tender young leaves are light grey-green and the back of old leaves are lilac-red.}', 'Good luck and happiness.', 4, 2, 4, 4);

INSERT INTO basic_plant_info(plant_id, botanical_name, known_as, species, description, poisonous, name_story, interesting_facts, symbolism, plant_characteristics_id, scientific_classification_id, plant_conditions_id, care_guide_id)
VALUES (5, 'lilium bulbiferum', '{"jimmys bane", "fire lily", "tiger lily"}', 'orange lily', "Orange lily (Lilium bulbiferum) is a flowering herbaceous lily species native to Europe. This species is toxic to cats. Its scientific name means bearing bulbs. Orange lily is the symbol of the Orange Order, a protestant fraternal order in Ireland.", true, '{}', '{"Flowers often symbolize certain emotions and meanings. The orange lily is a symbol of confidence, pride and even wealth. Giving the orange lily as a gift is the perfect sentiment for someone getting a job promotion or a recent graduate. Lilies have been symbolic in many cultures, even in ancient Greek mythology.}', 'It is generally thought of to represent emotions such as confidence and at the extreme, pride.', 5, 4, 5, 5);

INSERT INTO basic_plant_info(plant_id, botanical_name, known_as, species, description, poisonous, name_story, interesting_facts, symbolism, plant_characteristics_id, scientific_classification_id, plant_conditions_id, care_guide_id)
VALUES (6, 'opuntia microdasys', '{"bunny-ear prickly-pear", "cinnamon cactus", "bunny ears", "polka dot cactus", "bunny ears cactus", "bunny cactus"}', "angel's wings", "Angel's-wings (*Opuntia microdasys*) is a flowering cactus species native to Mexico. Angel's-wings is closely related to Opuntia RFID, which can be differentiated from Opuntia microdasys by its reddish color. Some scientists consider the two cacti to be the same species. This species is also known as the bunny ears cactus, the bunny cactus, or the polka-dot cactus. It is sometimes planted as an ornamental on borders and in gardens.", false, '{"Bunny ears cactus - It has become a popular houseplant because it is easy to take care of the plant and it has a cute appearance with great blooms. However, yellow flowers appear rarely. Despite this, it is very popular in cultivation because of the comical resemblance to a rabbits head. Therefore, it is called bunny ears cactus.}', '{}', 'Hope.', 6, 5, 6, 6);

INSERT INTO basic_plant_info(plant_id, botanical_name, known_as, species, description, poisonous, name_story, interesting_facts, symbolism, plant_characteristics_id, scientific_classification_id, plant_conditions_id, care_guide_id)
VALUES (7, 'epipremnum aureum', '{"devils ivy", "taro vine", "ceylon creeper", "ivy arum", "money plant", "hunters robe"}', 'golden pothos', "The golden pothos (Epipremnum aureum) is a popular houseplant that is commonly seen in Australia, Asia, and the West Indies. It goes by many nicknames, including devil's ivy, because it is so hard to kill and can even grow in low light conditions. Golden pothos has poisonous sap, so it should be kept away from pets and children.", true, '{"Golden pothos - Since its green leaves are laced with the color gold, it is called Golden Pothos. Epipremnum aureum has a long and tortuous naming history, and it has been reclassified several times. It was first classified as Pothos aureus. In 1962, scholars discovered that it actually had a blossoming phase. Since then, scholars closely observed and investigated the plants flowers, leaves and growth, and determined its latin name to be Epipremnum aureum.", "Devils ivy - Even if kept in the dark, it can stay green the whole year long, just like the tenacity of the devil. It is also known as devils ivy because it is almost impossible to kill."}', '{"The golden pothos is a popular houseplant, largely due to the way in which it cleans the air. In newly-renovated homes, the golden pothos has been shown to absorb benzene, tricholoethylene and formaldehyde, although you cannot rely on the plant alone to remove all harmful gases from a room. Nevertheless, they have tenacious vitality, and can quickly convert formaldehyde into sugars or amino acids, while decomposing any benzene emitted by photocopiers and printers, making it no surprise that this plant is often seen in homes and offices.}', "Happiness, good fortune, determination to achieve one's goals, wealth.", 7, 6, 7, 7);

